        });
    } else if (vix < 15) {
        symbols.push({
            symbol: 'VXX',
            type: 'Vol ETN',
            week2: 'BUY',
            month3: 'HOLD',
            month6: 'SELL',
            month18: 'SELL',
            month36: 'SELL',
            rationale: `VIX ${vix.toFixed(1)} - complacency. Buy vol for 1-2 week protection.`
        });
    }

    // Dollar moves - 1-2 week forex trades
    if (dxy > 105) {
        symbols.push({
            symbol: 'UUP',
            type: 'USD Bull',
            week2: 'SELL',
            month3: 'SELL',
            month6: 'HOLD',
            month18: 'HOLD',
            month36: 'HOLD',
            rationale: `DXY ${dxy.toFixed(1)} - overbought. Take profits on dollar longs.`
        });
        symbols.push({
            symbol: 'FXE',
            type: 'EUR Bull',
            week2: 'BUY',
            month3: 'BUY',
            month6: 'HOLD',
            month18: 'HOLD',
            month36: 'HOLD',
            rationale: `Euro oversold vs USD. 1-2 week bounce expected.`
        });
    } else if (dxy < 95) {
        symbols.push({
            symbol: 'UUP',
            type: 'USD Bull',
            week2: 'BUY',
            month3: 'BUY',
            month6: 'HOLD',
            month18: 'HOLD',
            month36: 'HOLD',
            rationale: `DXY ${dxy.toFixed(1)} - oversold. Dollar bounce likely.`
        });
    }

    // Credit spread changes - 1-2 week credit trades
    if (hySpread > 4.5) {
        symbols.push({
            symbol: 'HYG',
            type: 'High Yield',
            week2: 'BUY',
            month3: 'BUY',
            month6: 'HOLD',
            month18: 'BUY',
            month36: 'HOLD',
            rationale: `HY spreads ${hySpread}% - panic widening. 1-2 week mean reversion.`
        });
    } else if (hySpread < 3.0) {
        symbols.push({
            symbol: 'HYG',
            type: 'High Yield',
            week2: 'SELL',
            month3: 'HOLD',
