#!/usr/bin/env python3
"""
==============================================================================
SPARTAN LABS - BREAKTHROUGH INSIGHTS API
Serves breakthrough insights to frontend
==============================================================================
"""

from flask import Flask, jsonify, request
from flask_cors import CORS
from breakthrough_insights_engine import (
    run_breakthrough_analysis,
    save_insights_to_db,
    get_all_insights,
    ensure_insights_table
)
import logging
from datetime import datetime

app = Flask(__name__)
CORS(app)

logging.basicConfig(level=logging.INFO)
logger = logging.getLogger(__name__)

# ==============================================================================
# API ENDPOINTS
# ==============================================================================

@app.route('/api/insights/health', methods=['GET'])
def health_check():
    """Health check"""
    return jsonify({
        'status': 'healthy',
        'service': 'Breakthrough Insights API',
        'timestamp': datetime.now().isoformat()
    })

@app.route('/api/insights/list', methods=['GET'])
def list_insights():
    """Get all insights (newest first)"""
    try:
        limit = request.args.get('limit', 100, type=int)
        category = request.args.get('category', None)

        insights = get_all_insights(limit=limit)

        # Filter by category if specified
        if category and category != 'all':
            insights = [i for i in insights if i['category'] == category]

        return jsonify({
            'success': True,
            'count': len(insights),
            'insights': insights
        })

    except Exception as e:
        logger.error(f"Error listing insights: {e}")
        return jsonify({
            'success': False,
            'error': str(e)
        }), 500

@app.route('/api/insights/generate', methods=['POST'])
def generate_insights():
    """Generate new insights"""
    try:
        logger.info("ğŸš€ Starting insight generation...")

        # Run analysis
        new_insights = run_breakthrough_analysis()

        if not new_insights:
            return jsonify({
                'success': True,
                'message': 'No new insights found',
                'generated': 0,
                'saved': 0
            })
